import Calculator from "./Calculator";
import History from "./History";
import {useState} from 'react';
import {useParams} from 'react-router-dom';

function LoggedIn({setSession}) {
    const [calcHistory, setCalcHistory] = useState([]);
    const {id, name} = useParams();

    setSession({id: id, name: name}); 

    function handleLogout() {
        setSession({id:0, name: "there"});
        // setLoggedIn(false);
    }

    return(
        <>
            <h1>Hey {name}! {id !== 0 ? <button onClick={handleLogout}>logout</button> : null}</h1>
            <Calculator id={id} setCalcHistory={setCalcHistory}/>
            <History id={id} calcHistory={calcHistory}/>
        </>
        // list stuff the user only can see!
    )
}

export default LoggedIn;
