// Libraries
import { Switch, Route, useHistory } from 'react-router-dom';
import React, { useState } from "react";
import Cookies from 'universal-cookie';
// import styled from 'styled-components';

// import useSessionstorage from "@rooks/use-sessionstorage";

// Components
import NotLoggedIn from './NotLoggedIn';
import LoggedIn from './LoggedIn';

function App() {
    const [session, setSession] = useState({id:0, name: "there"});

    // const [ sessionStorages, setSessionStorage, removeSessionStorage ] = useSessionStorage(["logInfo"]);
    // function setSession(newItem){ setSessionStorage("logInfo", newItem); };
    // setSession({id: 0, name: "there"});
    const history = useHistory();

    function handleLogout() {
        setSession({id:0, name: "there"});
        // removeSessionStorage("logInfo");
        // setLoggedIn(false);
    }

    return (
        <>
            <h1>Hey {session.name}! {session.id !== 0 ? <button onClick={handleLogout}>logout</button> : null}</h1>
            {session.id !== 0  ? <h2>Big O Calculator</h2> : null}
            <Switch>
                <Route exact path="/">
                    {session.id !== 0 ? <LoggedIn id={session.id} /> : <NotLoggedIn history={history} setSession={setSession}/>}
                </Route>
                <Route path="/user/:id">
                    {session.id !== 0 ? <LoggedIn id={session.id} /> : <NotLoggedIn history={history} setSession={setSession}/>}
                </Route>
            </Switch>
        </>
    );
}

export default App;
