import Calculator from "./Calculator";
import History from "./History";
import {useEffect, useState} from 'react';
import {useParams} from 'react-router-dom';

function LoggedIn({calcHistory, setCalcHistory}) {    
    const {id, name} = useParams();

    function handleLogout() {
        setSession({id:0, name: "there"});
        setCalcHistory({});
        history.push('/')
    }

    return(
        <>
            <h2>Hey {name}! <button onClick={handleLogout}>logout</button></h2>
            <Calculator id={id} setCalcHistory={setCalcHistory}/>
            <History id={id} calcHistory={calcHistory}/>
        </>
    )
}

export default LoggedIn;
